import React, { useEffect, useRef, useState } from 'react';
import useStyles from './ServiceElements';
import { Box, Button, Grid, Typography } from '@material-ui/core';
import Card from './../Card';
import { motion } from 'framer-motion';
const useOnScreen = (options) => {
  const ref = useRef();
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      setVisible(entry.isIntersecting);
    }, options);
    if (ref.current) {
      observer.observe(ref.current);
    }
    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [ref, options]);
  return [ref, visible];
};

const Service = () => {
  const classes = useStyles();
  const [ref, visible] = useOnScreen({
    root: null,
  });
  const left = {
    hidden: {
      opacity: 0,
      x: -100,
    },
    vis: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 2,
      },
    },
  };

  const right = {
    hidden: {
      opacity: 0,
      x: 100,
    },
    vis: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 2,
      },
    },
  };
  return (
    <>
      <Box ref={ref} component='section' className={classes.box}>
        <Grid
          container
          spacing={2}
          className={classes.firstgrid}
          direction='column'
          alignItems='center'
        >
          <Grid item xs={10}>
            {visible ? (
              <motion.div variants={left} initial='hidden' animate='vis'>
                <Typography className={classes.h2} variant='h3'>
                  MY SERVICES
                </Typography>
              </motion.div>
            ) : (
              ''
            )}
          </Grid>
        </Grid>
        {visible ? (
          <motion.div variants={left} initial='hidden' animate='vis'>
            <Grid container justify='center' spacing={3}>
              <Grid item xs={10} sm={5} md={3}>
                <Card />
              </Grid>
              <Grid item xs={10} sm={5} md={3}>
                <Card />
              </Grid>
              <Grid item xs={10} sm={5} md={3}>
                <Card />
              </Grid>
            </Grid>
          </motion.div>
        ) : (
          ''
        )}
        <Grid
          container
          spacing={2}
          className={classes.lastgrid}
          direction='column'
          alignItems='center'
        >
          <Grid item xs={10}>
            {visible ? (
              <motion.div variants={left} initial='hidden' animate='vis'>
                <Button className={classes.btn} variant='contained'>
                  WATCH MORE
                </Button>
              </motion.div>
            ) : (
              ''
            )}
          </Grid>
        </Grid>
      </Box>
    </>
  );
};

export default Service;
