import { Box, Button, Grid, Typography } from '@material-ui/core';
import React, { useEffect } from 'react';
import { useInView } from 'react-intersection-observer';
import useStyles from './SectionElement';
import image from './../../videos/code.svg';
import { motion, useAnimation } from 'framer-motion';

const Section = () => {
  const classes = useStyles();
  const controls = useAnimation();
  const [ref, inView] = useInView();

  useEffect(() => {
    if (inView > '50%') {
      controls.start('visible');
    }
  }, [controls, inView]);
  return (
    <>
      <Box className={classes.box}>
        <Grid container className={classes.maincontainer} spacing='4'>
          <Grid item md={1} />
          <Grid item container direction='column' xs={10} md={5} spacing='4'>
            <Grid item>
              <motion.div
                ref={ref}
                animate={controls}
                initial='hidden'
                variants={{
                  visible: { opacity: 1 },
                  hidden: { opacity: 0, delay: 4 },
                }}
              >
                <Typography className={classes.h5} variant='h5'>
                  ABOUT
                </Typography>
              </motion.div>
            </Grid>
            <Grid item>
              <Typography variant='h4'>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Beatae,
                eum.
              </Typography>
            </Grid>
            <Grid item>
              <Button
                className={classes.btn}
                variant='contained'
                color='default'
              >
                COOL RIGHT
              </Button>
            </Grid>
          </Grid>

          <Grid container item xs={12} md={5}>
            <Grid item xs={1} md={0} />
            <Grid item xs={10}>
              <img className={classes.img} src={image} />
            </Grid>
            <Grid item xs={1} md={0} />
          </Grid>
          <Grid item md={1} />
        </Grid>
      </Box>
    </>
  );
};

export default Section;
