import { Box, Button, Grid, Typography } from '@material-ui/core';
import React, { useRef } from 'react';
import { useIntersection } from 'react-use';
import useStyles from './SectionElement';
import image from './../../videos/code.svg';
import { motion } from 'framer-motion';
const Section = () => {
  const classes = useStyles();
  const ref = useRef(null);
  const vis = 'false';
  const intersection = useIntersection(ref, {
    root: 'null',
    rootMargin: '0px',
    threshold: 1,
  });
  const hidden = {
    opacity: 0,
    x: -100,
  };
  const visible = {
    opacity: 1,
    x: 0,
  };
  const trans = {
    duration: 1,
    delay: 1,
  };
  return (
    <>
      <Box className={classes.box}>
        <Grid container className={classes.maincontainer} spacing='4'>
          <Grid item md={1} />
          <Grid item container direction='column' xs={10} md={5} spacing='4'>
            <Grid item>
              <motion.div
                ref={intersection}
                initial={hidden}
                animate={visible}
                transition={trans}
              >
                <Typography className={classes.h5} variant='h5'>
                  ABOUT
                </Typography>
              </motion.div>
            </Grid>
            <Grid item>
              <motion.div initial={hidden} animate={visible} transition={trans}>
                <Typography variant='h4'>
                  Lorem ipsum dolor sit amet consectetur adipisicing elit.
                  Beatae, eum.
                </Typography>
              </motion.div>
            </Grid>
            <Grid item>
              <motion.div initial={hidden} animate={visible} transition={trans}>
                <Button
                  className={classes.btn}
                  variant='contained'
                  color='default'
                >
                  COOL RIGHT
                </Button>
              </motion.div>
            </Grid>
          </Grid>

          <Grid container item xs={12} md={5}>
            <Grid item xs={1} md={0} />
            <Grid item xs={10}>
              <img className={classes.img} src={image} />
            </Grid>
            <Grid item xs={1} md={0} />
          </Grid>
          <Grid item md={1} />
        </Grid>
      </Box>
    </>
  );
};

export default Section;
