import {makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles((theme)=>({
    Box:{
        marginTop:'20vh',

    },
    img:{
        maxWidth:'100%'
    },
    firstgrid:{
        marginBottom:'10vh'
    },
    h2:{
        color:'green',
        letterSpacing:'.4rem',
        textAlign:'center',
        [theme.breakpoints.down('md')]:{
            fontSize:'2rem'
        }
    },

    next:{
        float:'right',
        border:'1px solid green',
        '&:hover':{
            border:'1px solid green',
            color:'green',
            outline:'none'
        }
    },
    pro:{
        marginTop:'15vh',
        marginBottom:'20vh'
    }
}))

export default useStyles
