import {makeStyles} from '@material-ui/core/styles'
const useStyles = makeStyles((theme)=>({
   box:{     
      overflow:'hidden',     
      marginTop:'-40px',

      [theme.breakpoints.down('md')]:{
         marginTop:'80px'
      }      
   },
   maincontainer:{
      display:'flex',
      alignItems:'center',
      justifyContent:'center'
   },
   h5:{
      color:'green',
      letterSpacing:'.6rem',

   },
   btn:{
      fontSize:'15px',
      letterSpacing:'.5rem',
      border:'1px solid transparent',

      '&:hover':{
         color:'green',
         border:'1px solid green',
         background:'#fff'
      }
   },
   img:{
      maxWidth:'100%',
     height:'100%',
     [theme.breakpoints.down('sm ')]:{    
        marginLeft:'22px',
      height:'400px'
   }  ,
   [theme.breakpoints.only('md ')]:{    
      marginLeft:'40px',




   },
}
}))


export default useStyles
