import {makeStyles} from '@material-ui/styles'


const useStyles = makeStyles((theme)=>({
    box:{
        position:'absolute',
        backgroundColor:'black',
        zIndex:'999',
        width:'100%',
        height:'100%',
        display:'grid',
        alignItems:'center',
        top:0,
        left:0,
        transition:'0.3s ease-in-out',

        top:'0',

    },
    close:{
        color:'#fff'
    },
    iconbutton:{
        position:'absolute',
        top:'1.2rem',
        right:'1.5rem',
        background:'transparent',
        fontSize:'2rem',
        cursor:'pointer',
        outline:'none'
    },
    list:{
        color:'#222',

    },
    listitem:{
        display:'block',
        justifyContent:'center',

    }
}))

export default useStyles
