import React, { useState } from 'react'
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext, Dot } from 'pure-react-carousel';
import useStyles from './CarouselElement'
import 'pure-react-carousel/dist/react-carousel.es.css'
import img from './../../videos/c.svg'
import img1 from './../../videos/b.svg'
import img2 from './../../videos/a.svg'
import { Box, Grid } from '@material-ui/core';
import Card from './../Card'



const Carousels = () => {
    const classes = useStyles()
    const [click,setClick]=useState(false)

const next=()=>{
    setClick(!click)
}


    return (

        <Box className={classes.box}>
         <Grid container alignContent='center' justify='center' >
       <button onClick={next} >Next</button>



         <Grid item xs={0} md={10} >
        <CarouselProvider 
        infinite={true}
        visibleSlides={3}
        isPlaying={true}
        interval={5000}
        naturalSlideWidth={100}
        naturalSlideHeight={100}
        totalSlides={5}

      >

           <Slider>
          {click ? <ButtonNext/> : <ButtonBack></ButtonBack> }
          <Slide index={0}>
             <Card/>
          </Slide>
          <Slide index={1}> <Card/> </Slide>
          <Slide index={2}> <Card/> </Slide>
          <Slide index={3}> <Card/></Slide>
          <Slide index={4}> <Card/> </Slide>
        </Slider>

      </CarouselProvider>
      </Grid>
     </Grid>

              </Box>
    )
}

export default Carousels
