import { Box, Button, Grid, Typography } from '@material-ui/core';
import React, { useEffect } from 'react';
import useStyles from './SectionElement';
import image from './../../videos/code.svg';
import { motion } from 'framer-motion';
import { useIntersection } from 'react-use';

const Section = () => {
  const classes = useStyles();
  const intersectionRef = React.useRef(null);
  const intersection = useIntersection(intersectionRef, {
    root: null,
    rootMargin: '0px',
    threshold: 1,
  });
  const init = {
    opacity: 0,
  };
  const fina = {
    opacity: 1,
  };
  const trans = {
    duration: 10,
  };
  return (
    <>
      <Box className={classes.box}>
        <Grid container className={classes.maincontainer} spacing='4'>
          <Grid item md={1} />
          <Grid item container direction='column' xs={10} md={5} spacing='4'>
            <Grid item>
              {intersection && intersection.intersectionRatio < 1 ? (
                <motion.div animate={fina} initial={init} transition={trans}>
                  <Typography className={classes.h5} variant='h5'>
                    ABOUT
                  </Typography>
                </motion.div>
              ) : (
                ''
              )}
            </Grid>
            <Grid item>
              <Typography variant='h4'>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Beatae,
                eum.
              </Typography>
            </Grid>
            <Grid item>
              <Button
                className={classes.btn}
                variant='contained'
                color='default'
              >
                COOL RIGHT
              </Button>
            </Grid>
          </Grid>

          <Grid container item xs={12} md={5}>
            <Grid item xs={1} md={0} />
            <Grid item xs={10}>
              <img className={classes.img} src={image} />
            </Grid>
            <Grid item xs={1} md={0} />
          </Grid>
          <Grid item md={1} />
        </Grid>
      </Box>
    </>
  );
};

export default Section;
