import { AppBar, Box, Button, IconButton, List, ListItem, ListItemText, Slide, Toolbar, Typography, useScrollTrigger } from '@material-ui/core'
import React, { useEffect, useState } from 'react'
import useStyles from './NavbarElement'
import MenuIcon from '@material-ui/icons/Menu';

function HideOnScroll(props) {
    const { children, window } = props;
    // Note that you normally won't need to set the window ref as useScrollTrigger
    // will default to window.
    // This is only being set here because the demo is in an iframe.
    const trigger = useScrollTriggerigger({ target: window ? window() : undefined });

    return (
      <Slide appear={false} direction="down" in={!trigger}>
        {children}
      </Slide>
    );
  }

const Navbar = ({toggle}) => {
    const classes = useStyles()
    const [navBackground, setNavBackground] = useState('appbar')
    const navRef = React.useRef()
    navRef.current = navBackground
    useEffect(() => {
        const handleScroll = () => {
            const show = window.scrollY > 80
            if (show) {
                setNavBackground('appbar1')
            } else {
                setNavBackground('appbar')
            }
        }
        document.addEventListener('scroll', handleScroll)
        return () => {
            document.removeEventListener('scroll', handleScroll)
        }
    }, [])
    return (
        <>

        <AppBar position='sticky' className={classes[navRef.current]} >
            <Toolbar className={classes.toolbar}>
                <Typography className={classes.typography}>
                    ACE
                </Typography>
                <Box display={{xs:'block',sm:'block',md:'none'}}>
                <IconButton >
          <MenuIcon onClick={toggle} className={classes.menuicon} />
          </IconButton>
          </Box>
         <Box display={{xs:'none', md:'block'}}>
          <List className={classes.list}>
              <ListItem className={classes.listitem}>
                  <ListItemText primary='HOME'/>
              </ListItem>
              <ListItem className={classes.listitem}>
                  <ListItemText primary='SERVICES'/>
              </ListItem>
              <ListItem className={classes.listitem}>
                  <ListItemText primary='CONTACT'/>
              </ListItem>
              <ListItem className={classes.listitem}>
                  <ListItemText primary='ABOUT'/>
              </ListItem>
              <ListItem>

              </ListItem>
          </List>
          </Box>
          <Box  display={ 'flex', { xs:'none', md:'block'}}>
          <Button className={classes.button} variant="contained">SIGN UP</Button>
          </Box>
            </Toolbar>
        </AppBar>

        </>
    )
}

export default Navbar
