import {makeStyles} from '@material-ui/styles'
import media from '@material-ui/core/useMediaQuery'
const matches = media('(min-width:600px)');
const useStyles = makeStyles((theme)=>({
   container:{
      position:'relative',
      display:'flex',
      alignItems:'center',
      justifyContent:'center',
      textAlign:'center',
      height:'120vh',
      overflow:'hidden'
   },
   video:{
      position:'absolute',
      top:'0',
      left:'0',
      right:'0',
      bottom:'0',
      overflow:'hidden',
      background:'#222',
      zIndex:'-1'
   },
   content:{
      zIndex:'1',
      color:'#fff'
   },
   h2:{
      color:'red',
   },
   matches:{
      'h2':{
         color:'green'
      }
   }
}))



export default useStyles
