import {makeStyles} from '@material-ui/styles'

const useStyles = makeStyles((theme)=>({
   container:{
      position:'relative',
      display:'flex',
      alignItems:'center',
      justifyContent:'center',
      textAlign:'center',
      height:'120vh',
      overflow:'hidden'
   },
   video:{
      position:'absolute',
      top:'0',
      left:'0',
      right:'0',
      bottom:'0',
      overflow:'hidden',
      background:'#222',
      zIndex:'-1'
   },
   content:{
      zIndex:'1',
      color:'#fff'
   },
   h2:{
      [theme.breapoints.down('sm')]:{
         color:'red'
      }
   }
}))

export default useStyles
