import {makeStyles} from '@material-ui/styles'


const useStyles = makeStyles((theme)=>({
    box:{
        position:'absolute',
        backgroundColor:'black',
        zIndex:'999',

       display:'grid',
        alignItems:'center',
        top:0,
        left:0,
        transition:'0.3s ease-in-out',

        top:'0',

    },
    close:{
        color:'#fff'
    },
    iconbutton:{
        position:'absolute',
        top:'1.2rem',
        right:'1.5rem',
        background:'transparent',
        cursor:'pointer',

    },
    list:{
        color:'#fff',


    },

    listitem:{
        display:'flex',
        justifyContent:'center',
        cursor:'pointer',
    },
    button:{

        fontSize:'1rem',
          transition:'all 0.3s ease-in-out',
          border:'1px solid #fff',

        '&:hover':{
            backgroundColor:'#222',
            color:'#fff',
            transition:'all 0.3s ease-in-out',
            border:'1px solid #fff'
        }

    }
}))

export default useStyles
